package com.teuskim.sbrowser;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;import android.util.Log;public class MD5 {	public static String encode(String input) {		MessageDigest md = null;		try {			md = MessageDigest.getInstance("MD5");		} catch(NoSuchAlgorithmException e) {			Log.e("MD5", "NoSuchAlgorithmException " + e.getMessage());		}		md.reset();		byte[] bInput = md.digest(input.getBytes());		StringBuffer sb = new StringBuffer();		for(int i = 0; i < bInput.length; i++) {			String hashChar = String.format("%02x", bInput[i] & 0x000000ff);			sb.append(hashChar);		}		return sb.toString();	}}